    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        ///Boton Jugar...
        try {
            ///Bonton Crear Usuario...
            String usuario =jTextField1.getText().toString();
            int agrego= cliente.Buscar_Usuario(usuario);
            if(agrego==1){
                Juego juego = new Juego();
                juego.setjuego(cliente,usuario);
                juego.setVisible(true);
                this.setVisible(false);
                
            }else{
                JOptionPane.showMessageDialog(null, "No se Guardo el Usuario", "Usuario", JOptionPane.INFORMATION_MESSAGE);
            }
            
        } catch (TException ex) {
            Logger.getLogger(VentanaPrincipal.class.getName()).log(Level.SEVERE, null, ex);
        }
        

        
    }//GEN-LAST:event_jButton2ActionPerformed

