    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        try {
            ///Bonton Crear Usuario...
            String usuario =jTextField1.getText().toString();
            int agrego= cliente.Agregar_Usuario(usuario);
            if(agrego==1){
                JOptionPane.showMessageDialog(null, "Se guardo su Usuario", "Usuario", JOptionPane.INFORMATION_MESSAGE);
                
            }else{
                JOptionPane.showMessageDialog(null, "No se Guardo el Usuario", "Usuario", JOptionPane.INFORMATION_MESSAGE);
            }
            
        } catch (TException ex) {
           JOptionPane.showMessageDialog(null,"No se encuantra el Servidor" , "Error", JOptionPane.INFORMATION_MESSAGE);
        }
        
    }//GEN-LAST:event_jButton3ActionPerformed

